//假数据，测试用
export default {
  data() {
    return {
      username: '这里填写用户名',
      notebooks: [],
      // 存储获取的笔记本详细信息
      currentPage: 1,
      totalPages: 1
    };
  },
  computed: {
    sortedNotebooks() {
      return this.notebooks.sort((a, b) => new Date(b.lastModified) - new Date(a.lastModified));
    },
    paginatedNotebooks() {
      const notebooksPerPage = 4;
      const startIndex = (this.currentPage - 1) * notebooksPerPage;
      const endIndex = startIndex + notebooksPerPage;
      return this.sortedNotebooks.slice(startIndex, endIndex);
    },
    totalPagesArray() {
      return Array.from({
        length: this.totalPages
      }, (v, k) => k + 1);
    }
  },
  methods: {
    goToStart() {
      this.$router.push({
        name: 'Start'
      });
    },
    goToNotebook() {
      this.$router.push({
        name: 'Notebook'
      });
    },
    goToCommunity() {
      this.$router.push({
        name: 'Community'
      });
    },
    goToNotebookDetails(notebook) {
      const notebookPath = `/notebook/${notebook.id}`;
      this.$router.push({
        path: notebookPath
      });
    },
    async editNotebookName(id) {
      //修改笔记本名称
      const newTitle = prompt("请输入新的笔记本名称：");
      if (newTitle) {
        try {
          const response = await fetch(`/api/notebooks/${id}`, {
            method: 'PUT',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify({
              title: newTitle
            })
          });
          const result = await response.json();
          if (result.code === 200) {
            if (1) {
              alert("笔记本名称修改成功");
              this.notebooks = this.notebooks.map(notebook => notebook.id === id ? {
                ...notebook,
                name: newTitle
              } : notebook);
            } else if (result.code === 501) {
              alert("token验证失败");
            } else if (result.code === 405) {
              alert("请求体不合法");
            } else if (result.code === 404) {
              alert("笔记本不存在");
            }
          }
        } catch (error) {
          console.error('Error editing notebook name:', error);
        }
      }
    },
    async editNotebookSummary(id) {
      //修改笔记本简介
      const newSummary = prompt("请输入新的笔记本简介：");
      if (newSummary) {
        try {
          const response = await fetch(`/api/notebooks/${id}`, {
            method: 'PUT',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify({
              summary: newSummary
            })
          });
          const result = await response.json();
          if (result.code === 200) {
            alert("笔记本简介修改成功");
            this.notebooks = this.notebooks.map(notebook => notebook.id === id ? {
              ...notebook,
              summary: newSummary
            } : notebook);
          } else if (result.code === 501) {
            alert("token验证失败");
          } else if (result.code === 405) {
            alert("请求体不合法");
          } else if (result.code === 404) {
            alert("笔记本不存在");
          }
        } catch (error) {
          console.error('Error editing notebook summary:', error);
        }
      }
    },
    async deleteNotebook(id) {
      if (confirm("确定要删除这个笔记本吗？")) {
        //删除笔记本
        try {
          const response = await fetch(`/api/notebooks/${id}`, {
            method: 'DELETE'
          });
          const result = await response.json();
          if (result.code === 200) {
            alert("笔记本删除成功");
            this.notebooks = this.notebooks.filter(notebook => notebook.id !== id);
            this.updateTotalPages(); // 更新总页数
          } else if (result.code === 501) {
            alert("token验证失败");
          } else if (result.code === 405) {
            alert("请求体不合法");
          } else if (result.code === 404) {
            alert("笔记本不存在");
          }
        } catch (error) {
          console.error('Error deleting notebook:', error);
        }
      }
    },
    async fetchNotebooks() {
      try {
        // 模拟延迟
        await new Promise(resolve => setTimeout(resolve, 500));
        // 模拟获取的笔记本ID列表
        const notebookIds = [1, 2, 3, 4, 5, 6];
        this.notebooks = await Promise.all(notebookIds.map(id => this.fetchNotebookDetails(id)));
        this.updateTotalPages(); // 更新总页数
      } catch (error) {
        console.error('Error fetching notebook IDs:', error);
      }
    },
